(1, 1, 0, 'tpl.AjaxForm.example', '', 0, 1, 0, '<form action=\"\" method=\"post\" class=\"ajax_form af_example\">\n\n    <div class=\"form-group\">\n        <label class=\"control-label\" for=\"af_name\">[[%af_label_name]]</label>\n        <div class=\"controls\">\n            <input type=\"text\" id=\"af_name\" name=\"name\" value=\"[[+fi.name]]\" placeholder=\"\" class=\"form-control\"/>\n            <span class=\"error_name\">[[+fi.error.name]]</span>\n        </div>\n    </div>\n\n    <div class=\"form-group\">\n        <label class=\"control-label\" for=\"af_email\">[[%af_label_email]]</label>\n        <div class=\"controls\">\n            <input type=\"email\" id=\"af_email\" name=\"email\" value=\"[[+fi.email]]\" placeholder=\"\" class=\"form-control\"/>\n            <span class=\"error_email\">[[+fi.error.email]]</span>\n        </div>\n    </div>\n\n    <div class=\"form-group\">\n        <label class=\"control-label\" for=\"af_message\">[[%af_label_message]]</label>\n        <div class=\"controls\">\n            <textarea id=\"af_message\" name=\"message\" class=\"form-control\" rows=\"5\">[[+fi.message]]</textarea>\n            <span class=\"error_message\">[[+fi.error.message]]</span>\n        </div>\n    </div>\n\n    <div class=\"form-group\">\n        <div class=\"controls\">\n            <button type=\"reset\" class=\"btn btn-default\">[[%af_reset]]</button>\n            <button type=\"submit\" class=\"btn btn-primary\">[[%af_submit]]</button>\n        </div>\n    </div>\n\n    [[+fi.success:is=`1`:then=`\n    <div class=\"alert alert-success\">[[+fi.successMessage]]</div>\n    `]]\n    [[+fi.validation_error:is=`1`:then=`\n    <div class=\"alert alert-danger\">[[+fi.validation_error_message]]</div>\n    `]]\n</form>', 0, NULL, 0, 'core/components/ajaxform/elements/chunks/chunk.example.tpl'),
(2, 0, 0, 'blogit.blog_row_tpl', 'Blogit - Created by MPThemes http://mpthemes.com', 0, 7, 0, '<section class=\"blog_row_container\">\r\n	<h2><a href=\"[[~[[+id]]]]\" title=\"[[+longtitle:strip_tags:empty=`[[+pagetitle:strip_tags]]`]]\">[[+longtitle:strip_tags:empty=`[[+pagetitle:strip_tags]]`]]</a></h2>\r\n	[[+blogit.post_main_image:isnot=``:then=`<a href=\"[[~[[+id]]]]\" title=\"[[+longtitle:strip_tags:empty=`[[+pagetitle:strip_tags]]`]]\"><img src=\"[[+blogit.post_main_image:phpthumbof=`w=[[++blogit.main_image_overview_width]]&h=[[++blogit.main_image_overview_height]]&zc=[[++blogit.main_image_overview_cz]]`]]\" alt=\"[[+blogit.post_main_image_alt]]\"></a>`]]\r\n	<p>[[+introtext]]</p>\r\n	<small>[[%blogit.published_on]]: <time datetime=\"[[+publishedon:strtotime:date=`%y-%m-%d`]]\">[[+publishedon:strtotime:date=`%b %d %Y`]]</time></small><br>\r\n	<small>[[%blogit.created_by]]: [[+createdby:userinfo=`username`]]</small><br>\r\n	[[++blogit.use_tags:isequalto=`1`:then=`\r\n	<small>Tags: [[!TaggerGetTags? &resources=`[[+id]]` &rowTpl=`blogit.tag_links_tpl` &separator=`, `]]</small>\r\n	`]]\r\n	<p><a href=\"[[~[[+id]]]]\">[[%blogit.readmore]]</a></p>\r\n</section>', 0, 'a:0:{}', 0, ''),
(3, 0, 0, 'blogit.tag_links_tpl', 'Blogit - Created by MPThemes http://mpthemes.com', 0, 7, 0, '<a href=\"[[+uri]]\">[[+tag]]</a>', 0, 'a:0:{}', 0, ''),
(4, 0, 0, 'blogit.container', 'Blogit - Created by MPThemes http://mpthemes.com', 0, 7, 0, '<!-- BlogIt - This chunk grabs all children of the resource. -->\n[[$blogit.get_posts]]\n<!-- BlogIt - This chunk grabs all uses Tagger tags used in posts -->\n[[$blogit.taglist]]', 0, 'a:0:{}', 0, ''),
(5, 0, 0, 'blogit.get_posts', 'Blogit - Created by MPThemes http://mpthemes.com', 0, 7, 0, '[[blogit.lexicon_load]]\n[[!getResources?\n	&parents=`[[*id]]`\n	&tpl=`blogit.blog_row_tpl`\n	&includeTVs=`1`\n	&processTVs=`1`\n	&tvPrefix=``\n	&where=`[[!TaggerGetResourcesWhere]]`\n]]', 0, 'a:0:{}', 0, ''),
(6, 0, 0, 'blogit.taglist', 'Blogit - Created by MPThemes http://mpthemes.com', 0, 7, 0, '[[++blogit.use_tags:isequalto=`1`:then=`\r\n<h4>[[%blogit.tags_header]]</h4>\r\n[[TaggerGetTags? &rowTpl=`blogit.tag_links_tpl` &separator=`, `]]\r\n`]]', 0, 'a:0:{}', 0, ''),
(7, 0, 0, 'fiDefaultEmailTpl', 'The default chunk used for the email. Please do not edit this chunk, as this will be overwritten when updating FormIt.', 0, 9, 0, '<p>[[+fields]]</p>', 0, 'a:0:{}', 0, ''),
(8, 0, 0, 'fiDefaultFiarTpl', 'The default chunk used for the autoresponder email. Please do not edit this chunk, as this will be overwritten when updating FormIt.', 0, 9, 0, '<p>Hello [[+name]],</p>\n\n<p>Your message has been received. We will respond as soon as possible. Thank you for contacting us.</p>\n\n<p>NOTE: This is an automatic response; please do not respond to this message directly.</p>\n\n<p>Here is your message:<br />\n[[+message:nl2br]]</p>', 0, 'a:0:{}', 0, ''),
(9, 0, 0, 'fiDefaultOptGroupTpl', 'The default chunk used by the FormItCountryOptions snippet for the select optgroup. Please do not edit this chunk, as this will be overwritten when updating FormIt.', 0, 9, 0, '<optgroup label=\"[[+text]]\">\n    [[+options]]\n</optgroup>', 0, 'a:0:{}', 0, ''),
(10, 0, 0, 'fiDefaultOptionTpl', 'The default chunk used by the FormItCountryOptions snippet for the select option. Please do not edit this chunk, as this will be overwritten when updating FormIt.', 0, 9, 0, '<option value=\"[[+value]]\"[[+selected]]>[[+text]]</option>', 0, 'a:0:{}', 0, ''),
(11, 0, 0, 'galAlbumRowTpl', '', 0, 10, 0, '<li[[+cls:notempty=` class=\"[[+cls]]\"`]]><a href=\"[[~[[*id]]? &[[+albumRequestVar]]=`[[+id]]`]]\">[[+showName:notempty=`[[+name]]`]]</a></li>', 0, '', 0, ''),
(12, 0, 0, 'galItemThumb', '', 0, 10, 0, '<div class=\"[[+cls]]\">\n    <a href=\"[[+linkToImage:if=`[[+linkToImage]]`:is=`1`:then=`[[+image_absolute]]`:else=`[[~[[*id]]?\n            &[[+imageGetParam]]=`[[+id]]`\n            &[[+albumRequestVar]]=`[[+album]]`\n            &[[+tagRequestVar]]=`[[+tag]]` ]]`]]\" title=\"[[+name]]\" [[+link_attributes]]>\n\n        <img class=\"[[+imgCls]]\" src=\"[[+thumbnail]]\" alt=\"[[+name]]\" [[+image_attributes]] />\n    </a>\n</div>', 0, '', 0, ''),
(13, 0, 0, 'lgnLoginTpl', '', 0, 11, 0, '<div class=\"loginForm\">\n    <div class=\"loginMessage\">[[+errors]]</div>\n    <div class=\"loginLogin\">\n        <form class=\"loginLoginForm\" action=\"[[~[[*id]]]]\" method=\"post\">\n            <span><a class=\"left\" href=\"[[~8]]\">Забыли пароль?</a></span><span> / </span><span> <a class=\"right\" href=\"[[~10]]\"> Регистрация </a ></span>\n            <fieldset class=\"loginLoginFieldset\">\n                \n                <label class=\"loginUsernameLabel\">\n                    <input class=\"loginUsername\" type=\"text\" name=\"username\" placeholder=\"Логин\" />\n                </label>\n                \n                <label class=\"loginPasswordLabel\">\n                    <input class=\"loginPassword\" type=\"password\" name=\"password\" placeholder=\"Пароль\" />\n                </label>\n                <input class=\"returnUrl\" type=\"hidden\" name=\"returnUrl\" value=\"[[+request_uri]]\" />\n\n                [[+login.recaptcha_html]]\n                \n                <input class=\"loginLoginValue\" type=\"hidden\" name=\"service\" value=\"login\" />\n                <span class=\"loginLoginButton\"><button type=\"submit\" name=\"Login\"></button></span>\n\n            </fieldset>\n        </form>\n    </div>\n</div>', 0, 'a:0:{}', 0, ''),
(14, 0, 0, 'lgnLogoutTpl', '', 0, 11, 0, '<div class=\"loginMessage\">[[+errors]]</div>\n<br />\n<div class=\"loginLogin\">\n    <div class=\"loginRegister\">\n        <div class=\"butt\"><a href=\"[[+logoutUrl]]\" title=\"[[+actionMsg]]\"><button class=\"out\"></button></a>\n       <span> Добро пожаловать <b>[[+modx.user.id:userinfo=`username`]]</b></span></div>\n        \n    </div>\n</div>', 0, 'a:0:{}', 0, ''),
(15, 0, 0, 'lgnErrTpl', '', 0, 11, 0, '<p class=\"error\">[[+msg]]</p>', 0, 'a:0:{}', 0, ''),
(16, 0, 0, 'lgnForgotPassEmail', '', 0, 11, 0, '<p>[[+username]],</p>\n\n<p>To activate your new password, please click on the following link:</p>\n\n<p><a href=\"[[+confirmUrl]]\">[[+confirmUrl]]</a></p>\n\n<p>If successful you can use the following password to login:</p>\n\n<p><strong>Password:</strong> [[+password]]</p>\n\n<p>If you did not request this message, please ignore it.</p>\n\n<p>Thanks,<br />\n<em>Site Administrator</em></p>', 0, 'a:0:{}', 0, ''),
(17, 0, 0, 'lgnForgotPassSentTpl', '', 0, 11, 0, '<h2>Your Login Information Has Been Sent</h2>\n\n<p>Your login information has been sent to the email address [[+email]].</p>', 0, 'a:0:{}', 0, ''),
(18, 0, 0, 'lgnForgotPassTpl', '', 0, 11, 0, '<div class=\"loginFPErrors\">[[+loginfp.errors]]</div>\n<div class=\"loginFP\">\n    <form class=\"loginFPForm\" action=\"[[~[[*id]]]]\" method=\"post\">\n        <fieldset class=\"loginFPFieldset\">\n            <legend class=\"loginFPLegend\">[[%login.forgot_password]]</legend>\n            <label class=\"loginFPUsernameLabel\">[[%login.username]]\n                <input class=\"loginFPUsername\" type=\"text\" name=\"username\" value=\"[[+loginfp.post.username]]\" />\n            </label>\n            \n            <p>[[%login.or_forgot_username]]</p>\n            \n            <label class=\"loginFPEmailLabel\">[[%login.email]]\n                <input class=\"loginFPEmail\" type=\"text\" name=\"email\" value=\"[[+loginfp.post.email]]\" />\n            </label>\n            \n            <input class=\"returnUrl\" type=\"hidden\" name=\"returnUrl\" value=\"[[+loginfp.request_uri]]\" />\n            \n            <input class=\"loginFPService\" type=\"hidden\" name=\"login_fp_service\" value=\"forgotpassword\" />\n            <span class=\"loginFPButton\"><input type=\"submit\" name=\"login_fp\" value=\"[[%login.reset_password]]\" /></span>\n        </fieldset>\n    </form>\n</div>', 0, 'a:0:{}', 0, ''),
(19, 0, 0, 'lgnResetPassTpl', '', 0, 11, 0, '<style>\n body{background-color:#f8e6c0}   \nsection.pwbreak{width:300px;margin:40px auto;}\nsection.pwbreak p{font-size: 18px}\n</style>\n<section class=\"pwbreak\">\n\n<div class=\"loginResetPass\">\n<p class=\"loginResetPassHeader\">[[+username]],</p>\n\n<p class=\"loginResetPassText center\">Ваш пароль был сброшен. Возвратитесь <a href=\"[[+loginUrl]]\">сюда</a> для авторизации.</p>  \n</div>\n</section>', 0, 'a:0:{}', 0, ''),
(20, 0, 0, 'lgnRegisterFormTpl', '', 0, 11, 0, '<div class=\"register\">\n    <div class=\"registerMessage\">[[+error.message]]</div>\n    \n    <form class=\"form\" action=\"[[~[[*id]]]]\" method=\"post\">\n        <input type=\"hidden\" name=\"nospam:blank\" value=\"\" />\n        \n        <label for=\"username\">[[%register.username? &namespace=`login` &topic=`register`]]\n            <span class=\"error\">[[+error.username]]</span>\n        </label>\n        <input type=\"text\" name=\"username:required:minLength=6\" id=\"username\" value=\"[[+username]]\" />\n        \n        <label for=\"password\">[[%register.password]]\n            <span class=\"error\">[[+error.password]]</span>\n        </label>\n        <input type=\"password\" name=\"password:required:minLength=6\" id=\"password\" value=\"[[+password]]\" />\n        \n        <label for=\"password_confirm\">[[%register.password_confirm]]\n            <span class=\"error\">[[+error.password_confirm]]</span>\n        </label>\n        <input type=\"password\" name=\"password_confirm:password_confirm=`password`\" id=\"password_confirm\" value=\"[[+password_confirm]]\" />\n        \n        <label for=\"fullname\">[[%register.fullname]]\n            <span class=\"error\">[[+error.fullname]]</span>\n        </label>\n        <input type=\"text\" name=\"fullname:required\" id=\"fullname\" value=\"[[+fullname]]\" />\n        \n        <label for=\"email\">[[%register.email]]\n            <span class=\"error\">[[+error.email]]</span>\n        </label>\n        <input type=\"text\" name=\"email:email\" id=\"email\" value=\"[[+email]]\" />\n        \n        <br class=\"clear\" />\n\n        [[+register.recaptcha_html]]\n        [[+error.recaptcha]]\n        \n        <div class=\"form-buttons\">\n            <input type=\"submit\" name=\"login-register-btn\" value=\"Register\" />\n        </div>\n    </form>\n</div>', 0, 'a:0:{}', 0, ''),
(21, 0, 0, 'lgnActivateEmailTpl', '', 0, 11, 0, '<p>[[+username]],</p>\n\n<p>Thanks for registering! To activate your new account, please click on the following link:</p>\n\n<p><a href=\"[[+confirmUrl]]\">[[+confirmUrl]]</a></p>\n\n<p>After activating, you may login with your password and username:</p>\n\n<p>\nUsername: <strong>[[+username]]</strong><br />\nPassword: <strong>[[+password]]</strong></p>\n\n<p>If you did not request this message, please ignore it.</p>\n\n<p>Thanks,<br />\n<em>Site Administrator</em></p>', 0, 'a:0:{}', 0, ''),
